@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Estilos para el panel de administración */
.admin-container {
  @apply p-0 bg-[#121821] text-white min-h-screen;
}

.admin-header {
  @apply flex justify-between items-center mb-6 flex-wrap gap-2;
}

.data-table {
  @apply w-full border-collapse;
}

.data-table th {
  @apply bg-[#16213e] text-white p-2 font-semibold text-sm uppercase sticky top-0 z-10;
}

.data-table td {
  @apply border-b border-[#2a2a42] p-2;
}

.admin-tabs {
  @apply flex bg-[#16213e] p-1 rounded-lg mb-4 overflow-x-auto whitespace-nowrap border-b border-[#2a2a42];
}

.admin-tab {
  @apply px-3 py-2 text-sm font-medium rounded-md transition-colors mx-1
         hover:bg-[#be0046] hover:text-white;
}

.admin-tab.active {
  @apply bg-[#be0046] text-white;
}

/* Estilos responsivos para móviles */
@media (max-width: 768px) {
  .admin-container {
    @apply p-0;
  }

  .admin-header {
    @apply flex-col items-start gap-3 p-3;
  }

  .admin-header h1 {
    @apply text-xl;
  }

  .button-group {
    @apply flex flex-wrap gap-2 justify-start;
  }

  .scrollable-container {
    @apply max-h-[calc(100vh-230px)] overflow-y-auto pb-40;
  }

  .session-controls {
    @apply flex flex-col gap-2;
  }

  .session-table {
    @apply text-xs;
  }

  .session-table th, .session-table td {
    @apply px-1 py-2;
  }

  .tabs-container button {
    @apply text-xs px-2 py-1;
