}

/* Estilo para resaltar elementos seleccionados */
.selected-row {
  @apply bg-[#0c1a2a] border-l-4 border-[#be0046];
}

/* Estilo para los botones principales */
.primary-button {
  @apply bg-[#FF6B35] hover:bg-[#FF8C5A] text-white px-4 py-2 rounded 
         transition-colors flex items-center justify-center gap-2;
}

.secondary-button {
  @apply bg-[#16213e] hover:bg-[#2a2a42] text-white px-4 py-2 rounded 
         transition-colors flex items-center justify-center gap-2;
}

/* Mejoras en el scrolling */
.scrollable-container {
  @apply overflow-y-auto max-h-[calc(100vh-220px)] overflow-x-hidden;
  scrollbar-width: thin;
  scrollbar-color: #be0046 #16213e;
  -webkit-overflow-scrolling: touch;
  padding-bottom: 120px; /* Espacio adicional para evitar que el contenido quede oculto */
}

.scrollable-container::-webkit-scrollbar {
  width: 8px; /* Barra más ancha para mejor usabilidad */
}

.scrollable-container::-webkit-scrollbar-track {
  background: #16213e;
  border-radius: 6px;
}

.scrollable-container::-webkit-scrollbar-thumb {
  background: #be0046;
  border-radius: 6px;
}

/* Animación para datos nuevos */
.new-data {
  animation: highlight 2s ease-in-out;
}

@keyframes highlight {
  0% {
    background-color: rgba(190, 0, 70, 0.2);
  }
  100% {
    background-color: transparent;
  }
}

/* Notificaciones mejoradas */
.notification-badge {
  @apply bg-[#be0046] text-white text-xs font-bold rounded-full h-5 w-5 flex items-center justify-center absolute -top-1 -right-1;
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0% {
    box-shadow: 0 0 0 0 rgba(190, 0, 70, 0.7);
  }
  70% {
    box-shadow: 0 0 0 6px rgba(190, 0, 70, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(190, 0, 70, 0);
