/************* MENU DE PRIMER NIVEL *************/
/* menu::base */
div#cmsn{
	background:url(../images/bg1.svg) right -717px no-repeat;
	height: 40px;
}
div#menu {
	height: 40px;
	padding-left: 15px;
	width:840px;
	float:left;
	background:url(../images/bg1.svg) -293px -480px no-repeat;
}
div#menu ul {
    margin: 0;
    padding: 0;
    list-style: none;
    float: left;
}
div#menu ul.menu {
    padding-right: 0px;
}
div#menu li {
    position: relative;
    margin: 0;
	margin-right:0px;/*separación pestañas*/
    padding: 0px 0px 0 0;
    display: block;
    float: left;
    z-index: 9;
    width: auto;
}
div#menu ul ul li {
    z-index: 10;
}
div#menu li:hover>div {
    visibility: visible;
}
div#menu a {
    position: relative;
    z-index: 11;
    height: 38px;
    display: block;
    float: left;
    text-decoration: none;
    white-space: nowrap;
    width: auto;
    text-align:center;
}
div#menu span {
    padding-left:5px;
	padding-right:9px;
	padding-top:10px;
	color: #fff;
    background: none;
    display: block;
	font-size:12px;
	line-height:21px;
    cursor: pointer;
    background-repeat: no-repeat;
    background-position: 95% 0;
    text-align: center;
}
/* menu::nivel1 */
div#menu a {
    padding: 0px 0px 0 0;
    line-height: 18px;
    height: 40px;
    margin-right: 0px;
    _margin-right: 0px;
    background: none;
}
div#menu a:hover {
    background:url(../images/bg1.svg) right -959px no-repeat;
}
div#menu a:hover span {
    background:url(../images/bg1.svg) -305px -521px no-repeat;
}
div#menu li.current a,
div#menu ul.menu>li:hover>a {
	color:#003066;
	background:url(../images/bg1.svg) right -959px no-repeat;

}
div#menu li.current a span,
div#menu ul.menu>li:hover>a span {
	color:#003066;
    background:url(../images/bg1.svg) -305px -521px no-repeat;
}
div#menu ul.menu>li:hover>a span {
	height: 30px;
}

div#menu li.last { background: none; }
/* lava lamp */
div#menu li.back {
	background:url(../images/bg1.svg) -305px -565px no-repeat;
    width: 5px;
    height: 40px;
    z-index: 8;
    position: absolute;
    padding: 0;
    margin: 0px 0 0 0;
}
div#menu li.back .left {
    padding:0;
    width:auto;
	background:url(../images/bg1.svg) right -1004px no-repeat;
    height: 40px;
    margin: 0 5px 0 5px;
    float: none;
    position: relative;
    top: 0;
    left: 0;
    visibility: visible;
}
