      <div className="flex justify-end space-x-2 mt-6">
        <Button 
          onClick={onClose}
          variant="secondary"
          className="bg-gray-600 text-white hover:bg-gray-700"
        >
          Cancelar
        </Button>
        <Button 
          onClick={handleSubmit}
          variant="default"
          className="bg-[#007bff] text-white hover:bg-opacity-90"
        >
          Continuar
        </Button>
      </div>
    </Modal>
  );
};

