function validateNumeric(obj,e){
	var key;
	var keychar;
	var value = obj.value;
	var returnval = false;
	if (window.event) key = e.keyCode; 
	else if(e.which) key = e.which; 
	else return true; 	
	if ((key >= 48  && key <= 57)|| key == 13 ||key == 8) {
		returnval = true;
	}
	else if (key == 32) {
		if (value.length >0) {
			if (value.charAt(value.length-1) != " ") returnval = true;
		}
		else returnval = false;
	}	
	return returnval;
}

