div#menu {
	height: 40px;
	padding-left: 15px;
	width:840px;
	float:left;
	background:url(../images/bg1.svg) -293px -480px no-repeat;
}
div#menu ul {
    margin: 0;
    padding: 0;
    list-style: none;
    float: left;
}
div#menu ul.menu {
    padding-right: 0px;
}
div#menu li {
    position: relative;
    margin: 0;
	margin-right:0px;/*separación pestañas*/
    padding: 0px 0px 0 0;
    display: block;
    float: left;
    z-index: 9;
    width: auto;
}
div#menu ul ul li {
    z-index: 10;
}
div#menu li:hover>div {
    visibility: visible;
}
div#menu a {
    position: relative;
    z-index: 11;
    height: 38px;
    display: block;
    float: left;
    text-decoration: none;
    white-space: nowrap;
    width: auto;
    text-align:center;
}
div#menu span {
    padding-left:5px;
	padding-right:9px;
	padding-top:10px;
	color: #fff;
    background: none;
    display: block;
	font-size:12px;
	line-height:21px;
    cursor: pointer;
    background-repeat: no-repeat;
    background-position: 95% 0;
    text-align: center;
}
