      .filter(sms => sms.userId === userId)
      .sort((a, b) => {
        const dateA = a.sentAt ? new Date(a.sentAt) : new Date();
        const dateB = b.sentAt ? new Date(b.sentAt) : new Date();
        return dateB.getTime() - dateA.getTime();
      });
  }

